import React, { Component } from 'react'

export class FetchTop10Players extends Component {
    constructor(props) {
        super(props)

        this.state = {
            projectList: [],
            Render: false
        }
    }

    async fetchAll() {
        try {

            let response = await (fetch(this.props.websiteUrl + 'api/top10-players/'));
            let data = await response.json();
            this.setState({
                projectList: data,
                Render: true
            })

        } catch (err) {
            console.log(err)
        }

    }
    componentWillMount() {
        this.fetchAll()
    }
    render() {
        if (this.state.Render == true) {

            return (
                <div>
                    {this.state.playerList.map(function (player, index) {
                        return (
                            <div key={index}>{player.id} , {player.country}, {player.nickname}, {player.score} </div>
                        )
                    })}
                </div>
            )

        } 

        else {
            return (
                <>Wait</>
            )
        }



    }
}

export default FetchTop10Players
