import React, { Component } from 'react'

export class FetchRandomWords extends Component {
    constructor(props) {
        super(props)
        // initializing the variables that I am going to use later.
        this.state = {
            randomWords: [],
            correctWordList: [],
            wrongWordList: [],
            Render: false,
            errors: false,
        }
    }
    async fetchRandomWords(level) {
        try {
            var totalWords = level * 4
            var wrongWords = totalWords / 4
            let response = await (fetch(this.props.websiteUrl + `api/get-random-words/total=${totalWords}/wrong=${wrongWords}`));
            let data = await response.json();
            this.setState({
                randomWords: data,
                // Rendering if the response that is coming from the API is okay.
                Render: true
            })

            // If there is any error handle it by changing the state Render:false and errors:true
        } catch (err) {
            this.setState({
                errors: true,
                Render: false
            })
        }

    }
    handleCorrectAnswer(lst) {
        var correctWordList = []
        var wrongWordList = [] 
        lst.map(function(word){
            // The words that ends with :C are the correct ones.
            if(word.slice(-2) == ':C') {
                correctWordList.push(word)
            } else {
                wrongWordList.push(word)
            }
        })
        this.setState({
            correctWordList: correctWordList,
            wrongWordList: wrongWordList
        })

    }
    componentWillMount() {
        // Fetching the data before render 
        this.fetchRandomWords(1)
    }
    render() {
        const { Render, errors, randomWords } = this.state
        // Rendering the data if this.state.Render is true as we set.
        if (Render) {
            return (
                <div>
                    {randomWords.map(function(word,index){
                        return(
                            <a key={index}>{word}</a>
                        )
                    })}
                </div>
            )
        }
        // If the data is taken too much time to load display a spinning animation
        else if (Render == false) {
            return (
                <>Wait</>
            )
        }
        // Error handling
        else if (errors) {
            return (
                <>Something is wrong. Please try again.</>
            )
        }
    }
}

export default FetchRandomWords
