import React, { Component } from 'react'
import Game from './Game'
export class FetchRandomWords extends Component {
    constructor(props) {
        super(props)
        // initializing the variables that I am going to use later.
        this.state = {
            level:0,
            randomWords: [],
            correctWordList: [],
            wrongWordList: [],
            Render: false,
            errors: false,

        }
        this.fetchRandomWords = this.fetchRandomWords.bind(this)
    }
    async fetchRandomWords(level) {
        try {
            var totalWords = level * 4
            var wrongWords = totalWords / 4
            let response = await (fetch(this.props.websiteUrl + `api/get-random-words/total=${totalWords}/wrong=${wrongWords}`));
            let data = await response.json();
            this.setState({
                // Rendering if the response that is coming from the API is okay.
                Render: true
            })
            this.handleCorrectAnswer(data)

            // If there is any error handle it by changing the state Render:false and errors:true.
        } catch (err) {
            this.setState({
                errors: true,
                Render: false
            })
        }

    }
    // Helper function to divide the list by correct ones and wrong ones.
    handleCorrectAnswer(lst) {
        var correctWordList = []
        var wrongWordList = [] 
        lst.map(function(word){
            // The words that ends with :C are the correct ones.

            if(word.slice(-2) == ':C') {
                let wordToPush = word.slice(0, word.length - 2)
                correctWordList.push(wordToPush)
            } else {
                wrongWordList.push(word)
            }
        })
        this.setState({
            correctWordList: correctWordList,
            wrongWordList: wrongWordList
        })

    }
    componentDidMount() {
        // Fetching the data before render.
        this.fetchRandomWords(1)
        this.handleCorrectAnswer(this.state.randomWords)
    }
    handleNextLevel() {
        this.setState({
            level: level + 1
        }, this.fetchRandomWords(this.state.level))
    }

    render() {
        const { Render, errors, randomWords, correctWordList, wrongWordList, level } = this.state
        // Rendering the data if this.state.Render is true as we set.
        if (Render) {
            return (
                // Passing the correct word list and wrong word list to handle the game in another component so the this component doesnt
                // get too complex and messy.
                <>
                <a>{correctWordList}</a>
                <a>{wrongWordList}</a>
                <br/>
                <button onClick={() =>Â this.handleNextLevel()}>Next Level</button>
                </>
            )
        }
        // If the data is taken too much time to load display a spinning animation.
        else if (Render == false) {
            return (
                <>Wait</>
            )
        }
        // Error handling
        else if (errors) {
            return (
                <>Something is wrong. Please try again.</>
            )
        }
    }
}

export default FetchRandomWords
